import { useEffect, useState } from "react";
import { fetchTodayTasks, addTask, updateTask, deleteTask } from "../api/tasks";

export default function TasksToday() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  useEffect(() => {
    listTasks({ is_today: true }).then((d) => setTasks(d.results));
  }, []);

  const addTask = async () => {
    if (!newTask.trim()) return;
    const t = await createTask({ title: newTask, is_today: true });
    setTasks([t, ...tasks]);
    setNewTask("");
  };

  const toggleDone = async (task) => {
    const updated = await updateTask(task.id, {
      status: task.status === "DONE" ? "TODO" : "DONE",
    });
    setTasks(tasks.map((t) => (t.id === task.id ? updated : t)));
  };

  return (
    <div className="p-4 bg-white rounded-2xl shadow">
      <h2 className="text-lg font-semibold mb-3">Today's Tasks</h2>

      <div className="flex gap-2 mb-3">
        <input
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add new task..."
          className="flex-1 border rounded-lg px-3 py-1 text-sm"
        />
        <button
          onClick={addTask}
          className="px-3 py-1 bg-blue-500 text-white rounded-lg text-sm"
        >
          Add
        </button>
      </div>

      <div className="space-y-2">
        {tasks.map((t) => (
          <div
            key={t.id}
            className="flex justify-between items-center p-2 rounded-lg border"
          >
            <button
              onClick={() => toggleDone(t)}
              className={`text-sm ${
                t.status === "DONE" ? "line-through text-gray-400" : ""
              }`}
            >
              {t.title}
            </button>
            <button
              onClick={() =>
                deleteTask(t.id).then(() =>
                  setTasks(tasks.filter((x) => x.id !== t.id))
                )
              }
              className="text-red-500 text-sm"
            >
              âœ•
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
