// src/components/QuoteWidget.jsx
import React, { useEffect, useState } from "react";

function QuoteWidget() {
  const [quote, setQuote] = useState(null);

  useEffect(() => {
    fetch("https://api.quotable.io/random")
      .then((res) => res.json())
      .then((data) => {
        setQuote({ text: data.content, author: data.author });
      })
      .catch((err) => console.error("Error fetching quote:", err));
  }, []);

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200 flex flex-col justify-center items-center">
      <h2 className="text-xl font-semibold mb-4">Inspiration</h2>
      {quote ? (
        <blockquote className="italic text-gray-700 text-center">
          “{quote.text}”
          <footer className="mt-2 text-sm text-gray-500">
            — {quote.author}
          </footer>
        </blockquote>
      ) : (
        <p className="text-gray-400">Loading...</p>
      )}
    </div>
  );
}

export default QuoteWidget;
