// src/components/WeatherWidget.jsx
import React, { useEffect, useState } from "react";

function WeatherWidget() {
  const [weather, setWeather] = useState(null);
  const [city, setCity] = useState("Delhi"); // default city
  const API_KEY = "af34049a6ae9a19c7511a6a40f85fe23"; // ðŸ”‘ Replace with your API key

  const fetchWeather = async () => {
    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      const data = await response.json();
      if (data.cod === 200) {
        setWeather({
          temp: data.main.temp,
          desc: data.weather[0].description,
          icon: data.weather[0].icon,
        });
      } else {
        setWeather(null);
      }
    } catch (error) {
      console.error("Error fetching weather:", error);
    }
  };

  useEffect(() => {
    fetchWeather();
  }, []);

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200">
      <h2 className="text-xl font-semibold mb-4 tracking-wide">Weather</h2>

      {/* City Input */}
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="Enter city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          className="border rounded-md px-3 py-2 text-sm flex-1"
        />
        <button
          onClick={fetchWeather}
          className="bg-blue-500 text-white px-4 py-2 rounded-md text-sm hover:bg-blue-600"
        >
          Get
        </button>
      </div>

      {/* Weather Info */}
      {weather ? (
        <div className="flex items-center gap-4">
          <img
            src={`https://openweathermap.org/img/wn/${weather.icon}@2x.png`}
            alt="Weather Icon"
          />
          <div>
            <p className="text-2xl font-bold">{weather.temp}Â°C</p>
            <p className="capitalize text-gray-600">{weather.desc}</p>
          </div>
        </div>
      ) : (
        <p className="text-gray-500 text-sm">Enter a valid city</p>
      )}
    </div>
  );
}

export default WeatherWidget;
