// src/components/MiniCalendar.jsx
import React from "react";

function MiniCalendar() {
  const now = new Date();
  const month = now.toLocaleString("default", { month: "long" });
  const year = now.getFullYear();
  const daysInMonth = new Date(year, now.getMonth() + 1, 0).getDate();

  const days = Array.from({ length: daysInMonth }, (_, i) => i + 1);

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200">
      <h2 className="text-xl font-semibold mb-4">Mini Calendar</h2>
      <p className="text-center text-lg font-medium text-gray-600 mb-2">
        {month} {year}
      </p>
      <div className="grid grid-cols-7 gap-2 text-center">
        {days.map((day) => (
          <div
            key={day}
            className="p-2 rounded-lg border text-gray-700 hover:bg-indigo-100 cursor-pointer"
          >
            {day}
          </div>
        ))}
      </div>
    </div>
  );
}

export default MiniCalendar;
