import React, { useEffect, useState } from "react";
import axios from "axios";
import Header from "../components/Header";
import PersonalCard from "../components/PersonalCard";
import WorkCard from "../components/WorkCard";
import WeatherWidget from "../components/WeatherWidget";
import TasksToday from "../components/TasksToday";
import TasksWeekly from "../components/TasksWeekly";
import InspirationCard from "../components/InspirationCard";
import CalendarCard from "../components/CalendarCard";
import NotesCard from "../components/NotesCard";
import QuoteWidget from "../components/QuoteWidget";

function Dashboard() {
  const [summary, setSummary] = useState({});
  const [tasksToday, setTasksToday] = useState([]);
  const [tasksWeekly, setTasksWeekly] = useState([]);
  const [inspiration, setInspiration] = useState(null);
  const [calendarEvents, setCalendarEvents] = useState([]);
  const [notes, setNotes] = useState([]);
  const [quote, setQuote] = useState(null);
  const [weather, setWeather] = useState(null);

  // Fetch functions for interactive components
  const fetchSummary = () => {
    axios.get("http://127.0.0.1:8000/api/summary/")
      .then(res => setSummary(res.data))
      .catch(err => console.error("Error fetching summary:", err));
  };

  const fetchTasksToday = () => {
    axios.get("http://127.0.0.1:8000/api/tasks/today/")
      .then(res => setTasksToday(res.data))
      .catch(err => console.error("Error fetching today's tasks:", err));
  };

  const fetchTasksWeekly = () => {
    axios.get("http://127.0.0.1:8000/api/tasks/weekly/")
      .then(res => setTasksWeekly(res.data))
      .catch(err => console.error("Error fetching weekly tasks:", err));
  };

  const fetchInspiration = () => {
    axios.get("http://127.0.0.1:8000/api/inspiration/")
      .then(res => setInspiration(res.data))
      .catch(err => console.error("Error fetching inspiration:", err));
  };

  const fetchCalendarEvents = () => {
    axios.get("http://127.0.0.1:8000/api/calendar/")
      .then(res => setCalendarEvents(res.data))
      .catch(err => console.error("Error fetching calendar events:", err));
  };

  const fetchNotes = () => {
    axios.get("http://127.0.0.1:8000/api/notes/")
      .then(res => setNotes(res.data))
      .catch(err => console.error("Error fetching notes:", err));
  };

  const fetchQuote = () => {
    axios.get("http://127.0.0.1:8000/api/quote/")
      .then(res => setQuote(res.data))
      .catch(err => console.error("Error fetching quote:", err));
  };

  const fetchWeather = () => {
    axios.get("http://127.0.0.1:8000/api/weather/")
      .then(res => setWeather(res.data))
      .catch(err => console.error("Error fetching weather:", err));
  };

  // Initial fetch
  useEffect(() => {
    fetchSummary();
    fetchTasksToday();
    fetchTasksWeekly();
    fetchInspiration();
    fetchCalendarEvents();
    fetchNotes();
    fetchQuote();
    fetchWeather();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-b from-pink-50 to-purple-50">
      <Header />

      <div className="p-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Top Cards */}
        <PersonalCard data={summary.personal} />
        <WorkCard data={summary.work} />
        <WeatherWidget data={weather} />

        {/* Tasks */}
        <TasksToday tasks={tasksToday} fetchTasks={fetchTasksToday} />
        <TasksWeekly tasks={tasksWeekly} fetchTasks={fetchTasksWeekly} />

        {/* Inspiration */}
        <InspirationCard data={inspiration} />

        {/* Calendar & Notes */}
        <CalendarCard events={calendarEvents} fetchEvents={fetchCalendarEvents} />
        <NotesCard notes={notes} fetchNotes={fetchNotes} />

        {/* Quote */}
        <QuoteWidget data={quote} />
      </div>
    </div>
  );
}

export default Dashboard;
