import React, { useEffect, useState } from "react";
import { fetchTodayTasks, addTask, updateTask, deleteTask } from "../api/tasks";

export default function TasksToday() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  useEffect(() => {
    loadTasks();
  }, []);

  const loadTasks = async () => {
    const data = await fetchTodayTasks();
    setTasks(data);
  };

  const handleAddTask = async () => {
    if (!newTask.trim()) return;
    await addTask({ title: newTask, completed: false });
    setNewTask("");
    loadTasks();
  };

  const handleToggleTask = async (task) => {
    await updateTask(task.id, { ...task, completed: !task.completed });
    loadTasks();
  };

  const handleDeleteTask = async (id) => {
    await deleteTask(id);
    loadTasks();
  };

  return (
    <div className="p-4 bg-white rounded-xl shadow-md">
      <h2 className="text-lg font-semibold mb-2">Today's Tasks</h2>
      <div className="flex mb-3">
        <input
          type="text"
          placeholder="Add new task..."
          className="border p-2 rounded flex-grow"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button
          onClick={handleAddTask}
          className="ml-2 bg-blue-500 text-white px-3 py-1 rounded"
        >
          Add
        </button>
      </div>
      <ul>
        {tasks.map((task) => (
          <li
            key={task.id}
            className="flex justify-between items-center mb-2"
          >
            <span
              onClick={() => handleToggleTask(task)}
              className={`cursor-pointer ${
                task.completed ? "line-through text-gray-500" : ""
              }`}
            >
              {task.title}
            </span>
            <button
              onClick={() => handleDeleteTask(task.id)}
              className="text-red-500 text-sm"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
