// src/components/WorkCard.jsx
import React, { useState } from "react";
import { FaTasks, FaProjectDiagram, FaFileAlt, FaUsers } from "react-icons/fa";

const workFeatures = [
  { icon: <FaTasks />, label: "Tasks" },
  { icon: <FaProjectDiagram />, label: "Projects" },
  { icon: <FaFileAlt />, label: "Reports" },
  { icon: <FaUsers />, label: "Meetings" },
];

function WorkCard() {
  const [selectedFeature, setSelectedFeature] = useState(null);
  const [event, setEvent] = useState("");
  const [events, setEvents] = useState({});

  const handleClick = (feature) => {
    setSelectedFeature(feature);
    setEvent(events[feature] || "");
  };

  const saveEvent = () => {
    setEvents({ ...events, [selectedFeature]: event });
    setSelectedFeature(null);
    setEvent("");
    // TODO: Replace with Axios call to Django API
  };

  const deleteEvent = () => {
    const updated = { ...events };
    delete updated[selectedFeature];
    setEvents(updated);
    setSelectedFeature(null);
    setEvent("");
    // TODO: Replace with delete API call
  };

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200">
      <h2 className="text-lg font-semibold mb-4 text-purple-600">Work</h2>
      <div className="grid grid-cols-2 gap-4">
        {workFeatures.map((f, idx) => (
          <div
            key={idx}
            onClick={() => handleClick(f.label)}
            className="flex flex-col items-center justify-center cursor-pointer transform transition-transform duration-300 hover:scale-110"
          >
            <div className="text-3xl text-purple-500">{f.icon}</div>
            <span className="mt-2 text-sm">{f.label}</span>
            {events[f.label] && (
              <p className="text-xs text-purple-400 mt-1">â€¢ {events[f.label]}</p>
            )}
          </div>
        ))}
      </div>

      {/* Modal for events */}
      {selectedFeature && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40">
          <div className="bg-white rounded-xl p-6 shadow-lg w-80">
            <h3 className="text-lg font-semibold mb-4 text-purple-600">
              {selectedFeature} Event
            </h3>
            <input
              type="text"
              value={event}
              onChange={(e) => setEvent(e.target.value)}
              className="w-full p-2 border rounded-md mb-3"
              placeholder={`Enter ${selectedFeature} details`}
            />
            <div className="flex justify-between">
              <button
                onClick={saveEvent}
                className="bg-purple-500 text-white px-4 py-2 rounded-md"
              >
                Save
              </button>
              <button
                onClick={deleteEvent}
                className="bg-red-500 text-white px-4 py-2 rounded-md"
              >
                Delete
              </button>
              <button
                onClick={() => setSelectedFeature(null)}
                className="bg-gray-300 px-4 py-2 rounded-md"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default WorkCard;
