import React, { useState, useEffect } from "react";
import api from "../api/api";  // use authenticated axios

const NotesCard = () => {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState("");

  // Fetch notes
  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const res = await api.get("/notes/");

        // SAFE CHECK: response must be array
        if (Array.isArray(res.data)) {
          setNotes(res.data);
        } else if (Array.isArray(res.data.results)) {
          setNotes(res.data.results);
        } else {
          console.log("Unexpected notes response:", res.data);
          setNotes([]);
        }
      } catch (err) {
        console.error("Error loading notes:", err.response?.data || err);
        setNotes([]); // prevent crash
      }
    };

    fetchNotes();
  }, []);

  // Add note
  const addNote = async () => {
    if (!newNote) return;

    try {
      const res = await api.post("/notes/", { content: newNote });

      if (res.data) setNotes([res.data, ...notes]);
      setNewNote("");
    } catch (err) {
      console.error("Error adding note:", err.response?.data || err);
    }
  };

  // Delete note
  const deleteNote = async (id) => {
    try {
      await api.delete(`/notes/${id}/`);
      setNotes(notes.filter((n) => n.id !== id));
    } catch (err) {
      console.error("Delete note error:", err);
    }
  };

  // Edit note
  const editNote = async (id, content) => {
    try {
      const res = await api.put(`/notes/${id}/`, { content });
      setNotes(notes.map((n) => (n.id === id ? res.data : n)));
    } catch (err) {
      console.error("Edit note error:", err);
    }
  };

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200 h-56">
      {/* Header */}
      <div className="bg-[#c8acc8] text-black p-2 rounded-t-2xl text-center font-bold">
        Notes
      </div>

      {/* Notes list */}
      <ul className="mt-2">
        {notes.length === 0 && (
          <p className="text-gray-400 text-sm text-center">No notes yet</p>
        )}

        {notes.map((note) => (
          <li
            key={note.id}
            className="flex justify-between items-center bg-gray-100 p-2 rounded-lg my-1"
          >
            <textarea
              defaultValue={note.content}
              onBlur={(e) => editNote(note.id, e.target.value)}
              className="bg-transparent flex-grow outline-none resize-none"
            />
            <button
              className="text-red-500 ml-2"
              onClick={() => deleteNote(note.id)}
            >
              âœ•
            </button>
          </li>
        ))}
      </ul>

      {/* Add Note */}
      <div className="flex mt-2">
        <input
          type="text"
          value={newNote}
          onChange={(e) => setNewNote(e.target.value)}
          className="flex-grow border p-2 rounded-lg"
          placeholder="Write a note..."
        />
        <button
          onClick={addNote}
          className="ml-2 bg-[#c8acc8] text-black px-3 py-2 rounded-lg"
        >
          Add
        </button>
      </div>
    </div>
  );
};

export default NotesCard;
