import { useEffect, useState } from "react";

function Quote() {
  const [quote, setQuote] = useState("");
  const [author, setAuthor] = useState("");
  const [loading, setLoading] = useState(true);

  // Fallback quotes if API fails
  const fallbackQuotes = [
    { content: "Believe you can and you're halfway there.", author: "Theodore Roosevelt" },
    { content: "The only limit to our realization of tomorrow is our doubts of today.", author: "Franklin D. Roosevelt" },
    { content: "Do what you can, with what you have, where you are.", author: "Theodore Roosevelt" },
    { content: "Act as if what you do makes a difference. It does.", author: "William James" },
  ];

  useEffect(() => {
    fetch("https://api.quotable.io/random")
      .then((res) => {
        if (!res.ok) throw new Error("Network response was not ok");
        return res.json();
      })
      .then((data) => {
        setQuote(data.content);
        setAuthor(data.author);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Quote fetch failed:", err);
        // Pick a random fallback quote
        const fallback = fallbackQuotes[Math.floor(Math.random() * fallbackQuotes.length)];
        setQuote(fallback.content);
        setAuthor(fallback.author);
        setLoading(false);
      });
  }, []);

  return (
    <div className="p-4 bg-white rounded shadow text-center">
      {loading ? (
        <p>Loading quote...</p>
      ) : (
        <>
          <p className="italic">"{quote}"</p>
          <p className="mt-2 font-semibold">â€” {author}</p>
        </>
      )}
    </div>
  );
}

export default Quote;
