// src/components/InspirationCard.jsx
import React, { useEffect, useState } from "react";

// Add your downloaded images in public/images/ folder
const images = [
  "/flower1.jpg",
  "/flower2.jpg",
  "/flower3.jpg", 
  "/flower4.jpg", 
  "/flower5.jpg", 
  "/flower6.jpg", 
  "/flower8.jpg", 
  "/flower7.jpg"
];

function InspirationCard() {
  const [image, setImage] = useState(null);

  useEffect(() => {
    // Pick a random image on refresh
    const randomImage = images[Math.floor(Math.random() * images.length)];
    setImage(randomImage);
  }, []);

  return (
    <div className="bg-white shadow-md rounded-2xl  border border-gray-250 h-70 flex items-center justify-center overflow-hidden">
      {image ? (
        <img
          src={image}
          alt="Inspiration"
          className="w-full h-full object-cover rounded-xl"
        />
      ) : (
        <p className="text-gray-400">Loading...</p>
      )}
    </div>
  );
}

export default InspirationCard;
