import { useState, useEffect } from "react";
import { motion } from "framer-motion";

// Custom hook for localStorage
function useLocalStorage(key, initialValue) {
  const [state, setState] = useState(() => {
    try {
      const stored = localStorage.getItem(key);
      return stored ? JSON.parse(stored) : initialValue;
    } catch {
      return initialValue;
    }
  });

  useEffect(() => {
    localStorage.setItem(key, JSON.stringify(state));
  }, [key, state]);

  return [state, setState];
}

// Calendar helper functions
const getDaysInMonth = (year, month) => {
  return new Date(year, month + 1, 0).getDate();
};

const CalendarCard = () => {
  const today = new Date();
  const [currentMonth, setCurrentMonth] = useState(today.getMonth());
  const [currentYear, setCurrentYear] = useState(today.getFullYear());
  const [events, setEvents] = useLocalStorage("calendarEvents", []);
  const [selectedDate, setSelectedDate] = useState("");
  const [eventInput, setEventInput] = useState("");
  const [editIndex, setEditIndex] = useState(null);

  const daysInMonth = getDaysInMonth(currentYear, currentMonth);
  const monthStart = new Date(currentYear, currentMonth, 1).getDay();

  // Filter events for a specific date
  const getEventsForDate = (dateStr) =>
    events.filter((ev) => ev.date === dateStr);

  const selectDate = (day) => {
    const dateStr = `${currentYear}-${String(currentMonth + 1).padStart(
      2,
      "0"
    )}-${String(day).padStart(2, "0")}`;
    setSelectedDate(dateStr);
    const existingEventIndex = events.findIndex((ev) => ev.date === dateStr);
    if (existingEventIndex >= 0) {
      setEventInput(events[existingEventIndex].event);
      setEditIndex(existingEventIndex);
    } else {
      setEventInput("");
      setEditIndex(null);
    }
  };

  const addOrEditEvent = () => {
    if (!selectedDate || !eventInput) return;
    if (editIndex !== null) {
      const newEvents = [...events];
      newEvents[editIndex] = { date: selectedDate, event: eventInput };
      setEvents(newEvents);
    } else {
      setEvents([...events, { date: selectedDate, event: eventInput }]);
    }
    setEventInput("");
    setSelectedDate("");
    setEditIndex(null);
  };

  const deleteEvent = () => {
    if (editIndex !== null) {
      setEvents(events.filter((_, idx) => idx !== editIndex));
      setEventInput("");
      setSelectedDate("");
      setEditIndex(null);
    }
  };

  return (
    <div className="p-4 bg-white rounded shadow h-60 overflow-y-auto">
      <h2 className="text-xl font-bold mb-4">Calendar</h2>

      {/* Month Navigation */}
      <div className="flex justify-between items-center mb-4">
        <button
          onClick={() =>
            setCurrentMonth((prev) => (prev === 0 ? 11 : prev - 1))
          }
          className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
        >
          Prev
        </button>
        <h3 className="font-semibold">
          {new Date(currentYear, currentMonth).toLocaleString("default", {
            month: "long",
          })}{" "}
          {currentYear}
        </h3>
        <button
          onClick={() =>
            setCurrentMonth((prev) => (prev === 11 ? 0 : prev + 1))
          }
          className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
        >
          Next
        </button>
      </div>

      {/* Calendar Grid */}
      <div className="grid grid-cols-7 gap-1 text-center">
        {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((d) => (
          <div key={d} className="font-semibold">
            {d}
          </div>
        ))}

        {/* Empty slots for month start */}
        {[...Array(monthStart)].map((_, i) => (
          <div key={"empty" + i}></div>
        ))}

        {/* Days */}
        {[...Array(daysInMonth)].map((_, i) => {
          const day = i + 1;
          const dateStr = `${currentYear}-${String(currentMonth + 1).padStart(
            2,
            "0"
          )}-${String(day).padStart(2, "0")}`;
          const hasEvent = getEventsForDate(dateStr).length > 0;
          return (
            <div
              key={day}
              onClick={() => selectDate(day)}
              className={`p-2 border rounded cursor-pointer ${
                selectedDate === dateStr ? "bg-blue-100" : ""
              } ${hasEvent ? "bg-yellow-100" : ""} hover:bg-blue-50`}
            >
              {day}
            </div>
          );
        })}
      </div>

      {/* Event Input */}
      {selectedDate && (
        <div className="mt-4">
          <h4 className="font-semibold mb-2">
            {editIndex !== null ? "Edit Event" : "Add Event"} for {selectedDate}
          </h4>
          <div className="flex gap-2 mb-2">
            <input
              type="text"
              value={eventInput}
              onChange={(e) => setEventInput(e.target.value)}
              placeholder="Event description"
              className="flex-1 p-2 border border-gray-300 rounded"
            />
            <button
              onClick={addOrEditEvent}
              className="bg-green-500 text-white px-4 rounded hover:bg-green-600"
            >
              {editIndex !== null ? "Save" : "Add"}
            </button>
            {editIndex !== null && (
              <button
                onClick={deleteEvent}
                className="bg-red-500 text-white px-4 rounded hover:bg-red-600"
              >
                Delete
              </button>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default CalendarCard;
