import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import TasksToday from "./TasksToday"; // Keep your existing TasksToday component

function PersonalCard() {
  const [activeSections, setActiveSections] = useState([]);

  // Inline placeholder components
  const Journal = () => <div>Your Journal content here</div>;
  const Goals = () => <div>Your Goals content here</div>;
  const Habits = () => <div>Your Habits content here</div>;

  const sections = [
    { name: "To-Do", icon: "üìù", component: <TasksToday /> },
    { name: "Journal", icon: "üìì", component: <Journal /> },
    { name: "Goals", icon: "üéØ", component: <Goals /> },
    { name: "Habits", icon: "üí™", component: <Habits /> },
  ];

  const toggleSection = (name) => {
    setActiveSections((prev) =>
      prev.includes(name) ? prev.filter((s) => s !== name) : [...prev, name]
    );
  };

  return (
    <div className="p-4 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Personal Hub</h2>

      {/* Animated Icons */}
      <div className="flex justify-around mb-6">
        {sections.map((section) => (
          <motion.div
            key={section.name}
            onClick={() => toggleSection(section.name)}
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.95 }}
            className={`flex flex-col items-center cursor-pointer p-2 rounded transition-colors ${
              activeSections.includes(section.name)
                ? "bg-gray-200"
                : "hover:bg-gray-100"
            }`}
          >
            <span className="text-2xl">{section.icon}</span>
            <span className="mt-1 text-sm">{section.name}</span>
          </motion.div>
        ))}
      </div>

      {/* Animated Section Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <AnimatePresence>
          {sections
            .filter((section) => activeSections.includes(section.name))
            .map((section) => (
              <motion.div
                key={section.name}
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
                className="p-4 bg-gray-50 rounded shadow"
              >
                {section.component}
              </motion.div>
            ))}
        </AnimatePresence>

        {activeSections.length === 0 && (
          <p className="text-gray-500 col-span-full text-center">
            Select a section above to view content
          </p>
        )}
      </div>
    </div>
  );
}

export default PersonalCard;
