import React, { useState, useEffect } from "react";
import api from "../api/api"; // ← USE authenticated axios
import {
  ClipboardList,
  FolderKanban,
  FileText,
  CalendarDays,
} from "lucide-react";

const WorkCard = () => {
  const [activeSection, setActiveSection] = useState(null);
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState("");

  // Fetch work events
  useEffect(() => {
    const fetchWorkEvents = async () => {
      try {
        const res = await api.get("/events/");

        // Must be array to avoid .map crash
        if (Array.isArray(res.data)) {
          setEvents(res.data.filter((e) => e.category?.startsWith("work-")));
        } else {
          setEvents([]);
        }
      } catch (err) {
        console.error("Error fetching work events:", err);
      }
    };

    fetchWorkEvents();
  }, []);

  // Add event
  const addEvent = async () => {
    if (!newEvent || !activeSection) return;

    try {
      const res = await api.post("/events/", {
        title: newEvent,
        category: `work-${activeSection}`,
      });

      setEvents([res.data, ...events]);
      setNewEvent("");
    } catch (err) {
      console.error("Error adding work event:", err.response?.data || err);
    }
  };

  // Delete event
  const deleteEvent = async (id) => {
    try {
      await api.delete(`/events/${id}/`);
      setEvents(events.filter((e) => e.id !== id));
    } catch (err) {
      console.error("Error deleting work event:", err);
    }
  };

  // Edit event
  const editEvent = async (id, newTitle) => {
    try {
      const res = await api.put(`/events/${id}/`, {
        title: newTitle,
        category: `work-${activeSection}`,
      });

      setEvents(events.map((e) => (e.id === id ? res.data : e)));
    } catch (err) {
      console.error("Error editing work event:", err);
    }
  };

  const sections = [
    { key: "tasks", label: "Tasks", icon: <ClipboardList size={28} /> },
    { key: "projects", label: "Projects", icon: <FolderKanban size={28} /> },
    { key: "reports", label: "Reports", icon: <FileText size={28} /> },
    { key: "meetings", label: "Meetings", icon: <CalendarDays size={28} /> },
  ];

  return (
    <div className="bg-white p-4 rounded-2xl shadow-md">
      {/* Header */}
      <div className="bg-[#c8acc8] text-black p-2 rounded-t-2xl text-center font-bold">
        Work
      </div>

      {/* Icons */}
      <div className="grid grid-cols-4 gap-4 justify-items-center py-4">
        {sections.map((section) => (
          <button
            key={section.key}
            onClick={() => setActiveSection(section.key)}
            className={`flex flex-col items-center transform transition-transform hover:scale-125 ${
              activeSection === section.key
                ? "text-purple-600"
                : "text-gray-600"
            }`}
          >
            {section.icon}
            <span className="text-xs">{section.label}</span>
          </button>
        ))}
      </div>

      {/* Event Section */}
      {activeSection && (
        <div className="mt-4">
          <h3 className="font-semibold capitalize">
            Work {activeSection}
          </h3>

          <ul className="mt-2">
            {events
              .filter((e) => e.category === `work-${activeSection}`)
              .map((event) => (
                <li
                  key={event.id}
                  className="flex justify-between items-center bg-gray-100 p-2 rounded-lg my-1"
                >
                  <input
                    type="text"
                    defaultValue={event.title}
                    onBlur={(e) => editEvent(event.id, e.target.value)}
                    className="bg-transparent flex-grow outline-none"
                  />
                  <button
                    className="text-red-500 ml-2"
                    onClick={() => deleteEvent(event.id)}
                  >
                    ✕
                  </button>
                </li>
              ))}
          </ul>

          {/* Add new event */}
          <div className="flex mt-2">
            <input
              type="text"
              value={newEvent}
              onChange={(e) => setNewEvent(e.target.value)}
              className="flex-grow border p-2 rounded-lg"
              placeholder={`Add new ${activeSection}...`}
            />
            <button
              onClick={addEvent}
              className="ml-2 bg-[#c8acc8] text-black px-3 py-2 rounded-lg"
            >
              Add
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkCard;
