// src/pages/Dashboard.jsx
import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import PersonalCard from "../components/PersonalCard";
import WorkCard from "../components/WorkCard";
import TasksToday from "../components/TasksToday";
import TasksWeekly from "../components/TasksWeekly";
import CalendarCard from "../components/CalendarCard";
import NotesCard from "../components/NotesCard";
import InspirationCard from "../components/InspirationCard";
import WeatherWidget from "../components/WeatherWidget"; 

import { getDashboardSummary } from "../api/summary";

export default function Dashboard() {
  const [summary, setSummary] = useState(null);

  useEffect(() => {
    async function fetchSummary() {
      try {
        const data = await getDashboardSummary();
        setSummary(data);
      } catch (error) {
        console.error("Failed to fetch dashboard summary:", error);
      }
    }
    fetchSummary();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100">
      <Header />

      <div className="p-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Left side */}
        <div className="space-y-6">
          <PersonalCard summary={summary?.personal} />
          <WorkCard summary={summary?.work} />
        </div>

        {/* Middle section */}
        <div className="space-y-6">
          <TasksToday tasks={summary?.todayTasks} />
          <TasksWeekly tasks={summary?.weeklyTasks} />
          <CalendarCard events={summary?.events} />
        </div>

        {/* Right side */}
        <div className="space-y-6">
          <NotesCard notes={summary?.notes} />
          <InspirationCard quote={summary?.quote} />
          <WeatherWidget />
        </div>
      </div>
    </div>
  );
}
