import React from "react";
import { Card, CardContent } from "../components/ui/card";
import { Briefcase, ClipboardList, FileText, Users } from "lucide-react";

const WorkCard = ({ projects = [], reports = [], meetings = [] }) => {
  return (
    <Card className="p-4 rounded-2xl shadow-md bg-white">
      <h2 className="text-xl font-semibold mb-3">Work Space</h2>
      <CardContent className="space-y-3">
        {/* Projects */}
        <div>
          <h3 className="flex items-center gap-2 font-medium text-gray-700">
            <Briefcase size={18} /> Projects
          </h3>
          {projects.length > 0 ? (
            <ul className="list-disc pl-6 text-gray-600">
              {projects.slice(0, 3).map((project) => (
                <li key={project.id}>{project.title}</li>
              ))}
            </ul>
          ) : (
            <p className="text-sm text-gray-400">No projects available</p>
          )}
        </div>

        {/* Reports */}
        <div>
          <h3 className="flex items-center gap-2 font-medium text-gray-700">
            <FileText size={18} /> Reports
          </h3>
          {reports.length > 0 ? (
            <ul className="list-disc pl-6 text-gray-600">
              {reports.slice(0, 3).map((report) => (
                <li key={report.id}>{report.title}</li>
              ))}
            </ul>
          ) : (
            <p className="text-sm text-gray-400">No reports available</p>
          )}
        </div>

        {/* Meetings */}
        <div>
          <h3 className="flex items-center gap-2 font-medium text-gray-700">
            <Users size={18} /> Meetings
          </h3>
          {meetings.length > 0 ? (
            <ul className="list-disc pl-6 text-gray-600">
              {meetings.slice(0, 3).map((meeting) => (
                <li key={meeting.id}>
                  {meeting.title} ({meeting.date})
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-sm text-gray-400">No meetings scheduled</p>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

export default WorkCard;
