import React, { useEffect, useState } from "react";

export default function QuoteWidget() {
  const [quote, setQuote] = useState(null);

  useEffect(() => {
    fetch("https://api.quotable.io/random")
      .then((res) => res.json())
      .then((data) => setQuote(data))
      .catch(() => {
        // If API fails, fallback to a static quote
        setQuote({
          content: "Stay positive, keep coding!",
          author: "Anonymous",
        });
      });
  }, []);

  if (!quote) return <p className="text-gray-500">Loading quote...</p>;

  return (
    <div className="p-4 bg-yellow-100 rounded-2xl shadow">
      <p className="italic">"{quote.content}"</p>
      <p className="text-right mt-2">â€” {quote.author}</p>
    </div>
  );
}
