import { useEffect, useState } from "react";
import api from "@/api/client";

export default function CalendarCard() {
  const today = new Date().toISOString().split("T")[0];
  const [date, setDate] = useState(today);
  const [events, setEvents] = useState([]);
  const [newTitle, setNewTitle] = useState("");

  useEffect(() => {
    api.get("/calendar-events/", { params: { date } })
      .then((r) => setEvents(r.data.results || r.data))
      .catch(console.error);
  }, [date]);

  const addEvent = async () => {
    if (!newTitle.trim()) return;
    const { data } = await api.post("/calendar-events/", {
      title: newTitle,
      date,
      all_day: true,
    });
    setEvents([data, ...events]);
    setNewTitle("");
  };

  const deleteEvent = async (id) => {
    await api.delete(`/calendar-events/${id}/`);
    setEvents(events.filter((e) => e.id !== id));
  };

  return (
    <div className="p-4 bg-white rounded-2xl shadow">
      <h2 className="text-lg font-semibold mb-3">Calendar</h2>

      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        className="mb-3 border rounded-lg px-2 py-1 text-sm"
      />

      <div className="flex gap-2 mb-3">
        <input
          value={newTitle}
          onChange={(e) => setNewTitle(e.target.value)}
          placeholder="Add event..."
          className="flex-1 border rounded-lg px-3 py-1 text-sm"
        />
        <button
          onClick={addEvent}
          className="px-3 py-1 bg-blue-500 text-white rounded-lg text-sm"
        >
          Add
        </button>
      </div>

      <div className="space-y-2">
        {events.map((e) => (
          <div
            key={e.id}
            className="flex justify-between items-center p-2 rounded-lg border"
          >
            <div className="text-sm">{e.title}</div>
            <button
              onClick={() => deleteEvent(e.id)}
              className="text-red-500 text-sm"
            >
              âœ•
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
