import React, { useState } from "react";
import axios from "axios";

const TasksWeekly = ({ tasks, fetchTasks }) => {
  const [newTask, setNewTask] = useState("");

  const handleAdd = () => {
    if (!newTask) return;
    axios.post("http://127.0.0.1:8000/api/tasks/weekly/", { title: newTask })
      .then(() => {
        setNewTask("");
        fetchTasks();
      })
      .catch(err => console.error("Error adding weekly task:", err));
  };

  const handleDelete = (id) => {
    axios.delete(`http://127.0.0.1:8000/api/tasks/weekly/${id}/`)
      .then(() => fetchTasks())
      .catch(err => console.error("Error deleting weekly task:", err));
  };

  return (
    <div className="bg-white rounded-2xl shadow p-4">
      <h2 className="text-xl font-semibold mb-2">Weekly Tasks</h2>

      <div className="flex gap-2 mb-2">
        <input
          type="text"
          placeholder="Add new task"
          className="border rounded p-1 flex-1"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button onClick={handleAdd} className="bg-purple-500 text-white px-3 rounded">Add</button>
      </div>

      {tasks.length ? (
        <ul className="list-disc list-inside">
          {tasks.map(task => (
            <li key={task.id} className="flex justify-between items-center">
              <span>{task.title}</span>
              <button onClick={() => handleDelete(task.id)} className="text-red-500">Delete</button>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-gray-400">No tasks this week.</p>
      )}
    </div>
  );
};

export default TasksWeekly;
