// src/components/InspirationCard.jsx
import React, { useEffect, useState } from "react";

function InspirationCard() {
  const [bgImage, setBgImage] = useState("");

  useEffect(() => {
    const images = ["/inspiration/1.jpg", "/inspiration/2.jpg", "/inspiration/3.jpg"];
    const randomImage = images[Math.floor(Math.random() * images.length)];
    setBgImage(randomImage);
  }, []); // changes only on refresh

  return (
    <div
      className="relative bg-white shadow-md rounded-2xl overflow-hidden h-56 flex items-center justify-center"
      style={{
        backgroundImage: `url(${bgImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div className="absolute inset-0 bg-black bg-opacity-30"></div>
      <div className="relative text-white text-xl font-semibold">
        Inspiration
      </div>
    </div>
  );
}

export default InspirationCard;
