import { useEffect, useState } from "react";
import api from "@/api/client";

export default function Reports() {
  const [reports, setReports] = useState([]);
  const [newReport, setNewReport] = useState("");

  useEffect(() => {
    api.get("/reports/?ordering=-created_at")
      .then((r) => setReports(r.data.results || r.data))
      .catch(console.error);
  }, []);

  const addReport = async () => {
    if (!newReport.trim()) return;
    const { data } = await api.post("/reports/", {
      title: newReport,
      content: "",
    });
    setReports([data, ...reports]);
    setNewReport("");
  };

  return (
    <div className="p-4 bg-white rounded-2xl shadow">
      <h2 className="text-lg font-semibold mb-3">Reports</h2>

      <div className="flex gap-2 mb-3">
        <input
          value={newReport}
          onChange={(e) => setNewReport(e.target.value)}
          placeholder="New report title..."
          className="flex-1 border rounded-lg px-3 py-1 text-sm"
        />
        <button
          onClick={addReport}
          className="px-3 py-1 bg-blue-500 text-white rounded-lg text-sm"
        >
          Add
        </button>
      </div>

      <div className="space-y-2">
        {reports.map((r) => (
          <div
            key={r.id}
            className="p-2 rounded-lg border"
          >
            <div className="text-sm font-medium">{r.title}</div>
            <div className="text-xs text-gray-500">{r.created_at}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
