import { useEffect, useState } from "react";

export default function WeatherWidget() {
  const [weather, setWeather] = useState(null);

  useEffect(() => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=Delhi&appid=af34049a6ae9a19c7511a6a40f85fe23
&units=metric`
    )
      .then((res) => res.json())
      .then((data) => {
        if (data.cod === 200) setWeather(data);
      else setWeather(null);
    })
    .catch(() => setWeather(null));
      
  }, []);

  return (
    <div className="bg-white shadow-md rounded-2xl p-4">
      <h2 className="text-xl font-semibold mb-2">üå§Ô∏è Weather</h2>
      {weather ? (
        <div>
          <p className="text-lg">{weather.name}</p>
          <p className="text-2xl font-bold">{weather.main.temp}¬∞C</p>
          <p>{weather.weather[0].description}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}
