// src/components/QuoteWidget.jsx
import React, { useEffect, useState } from "react";

function InspirationCard() {
  const [quote, setQuote] = useState(null);
  const [bgImage, setBgImage] = useState("");

  useEffect(() => {
    // Fetch random quote
    fetch("https://api.quotable.io/random")
      .then((res) => res.json())
      .then((data) => setQuote({ text: data.content, author: data.author }))
      .catch((err) => console.error("Error fetching quote:", err));

    // Pick random background image
    const images = ["/inspiration/1.jpg", "/inspiration/2.jpg", "/inspiration/3.jpg"];
    const randomImage = images[Math.floor(Math.random() * images.length)];
    setBgImage(randomImage);
  }, []);

  return (
    <div
      className="relative bg-white shadow-md rounded-2xl overflow-hidden h-48 flex items-center justify-center p-4"
      style={{
        backgroundImage: `url(${bgImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div className="absolute inset-0 bg-black bg-opacity-40"></div>
      <div className="relative text-center text-white px-4">
        <h2 className="text-xl font-semibold mb-2">Inspiration</h2>
        {quote ? (
          <blockquote className="italic text-lg">
            “{quote.text}”
            <footer className="mt-2 text-sm">— {quote.author}</footer>
          </blockquote>
        ) : (
          <p className="text-gray-200">Loading...</p>
        )}
      </div>
    </div>
  );
}

export default InspirationCard;
