import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import PersonalCard from "../components/PersonalCard";
import WorkCard from "../components/WorkCard";
import TasksToday from "../components/TasksToday";
import TasksWeekly from "../components/TasksWeekly";
import CalendarCard from "../components/CalendarCard";
import NotesCard from "../components/NotesCard";
import InspirationCard from "../components/InspirationCard";
import WeatherWidget from "../components/WeatherWidget";
import useSummary from "../hooks/useSummary";


const Dashboard = () => {
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadSummary = async () => {
      try {
        // ðŸ”¹ Replace with your actual auth token logic
        const token = localStorage.getItem("authToken"); 
        const data = await fetchSummary(token);
        setSummary(data);
      } catch (error) {
        console.error("Failed to load summary:", error);
      } finally {
        setLoading(false);
      }
    };

    loadSummary();
  }, []);

  if (loading) {
    return <div className="p-6">Loading dashboard...</div>;
  }

  if (!summary) {
    return <div className="p-6">No data available</div>;
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Header />
      <main className="p-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Personal Section */}
        <PersonalCard notes={summary.notes} quotes={summary.quotes} />

        {/* Work Section */}
        <WorkCard 
          projects={summary.projects}
          reports={summary.reports}
          meetings={summary.meetings}
        />

        {/* Tasks */}
        <TasksToday tasks={summary.tasks} />
        <TasksWeekly tasks={summary.tasks} />

        {/* Calendar */}
        <CalendarCard events={summary.events} />

        {/* Notes */}
        <NotesCard notes={summary.notes} />

        {/* Quotes / Inspiration */}
        <InspirationCard quotes={summary.quotes} />

        {/* Weather */}
        <WeatherWidget />
      </main>
    </div>
  );
};

export default Dashboard;
