// src/components/QuoteWidget.jsx
import React, { useEffect, useState } from "react";

function QuoteWidget() {
  const [quote, setQuote] = useState(null);
  const [author, setAuthor] = useState("");

  const fetchQuote = async () => {
    try {
      const response = await fetch("https://api.quotable.io/random");
      const data = await response.json();
      setQuote(data.content);
      setAuthor(data.author);
    } catch (error) {
      console.error("Error fetching quote:", error);
      setQuote("Keep pushing forward, no matter what!");
      setAuthor("Unknown");
    }
  };

  useEffect(() => {
    fetchQuote();
  }, []);

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200 flex flex-col justify-between">
      <h2 className="text-xl font-semibold mb-4 tracking-wide">Quote of the Day</h2>

      {quote ? (
        <div>
          <p className="italic text-gray-700 mb-2">"{quote}"</p>
          <p className="text-sm text-gray-500 text-right">â€” {author}</p>
        </div>
      ) : (
        <p className="text-gray-500 text-sm">Loading...</p>
      )}

      <button
        onClick={fetchQuote}
        className="mt-4 bg-purple-500 text-white px-4 py-2 rounded-md text-sm hover:bg-purple-600"
      >
        New Quote
      </button>
    </div>
  );
}

export default QuoteWidget;
