import React, { useState } from "react";
import axios from "axios";

const NotesCard = ({ notes, fetchNotes }) => {
  const [newNote, setNewNote] = useState("");

  const handleAdd = () => {
    if (!newNote) return;
    axios.post("http://127.0.0.1:8000/api/notes/", { text: newNote })
      .then(() => {
        setNewNote("");
        fetchNotes();
      })
      .catch(err => console.error("Error adding note:", err));
  };

  const handleDelete = (id) => {
    axios.delete(`http://127.0.0.1:8000/api/notes/${id}/`)
      .then(() => fetchNotes())
      .catch(err => console.error("Error deleting note:", err));
  };

  return (
    <div className="bg-white rounded-2xl shadow p-4">
      <h2 className="text-xl font-semibold mb-2">Notes</h2>

      <div className="flex gap-2 mb-2">
        <input 
          type="text" 
          placeholder="Add note" 
          className="border rounded p-1 flex-1"
          value={newNote} 
          onChange={(e) => setNewNote(e.target.value)} 
        />
        <button onClick={handleAdd} className="bg-purple-500 text-white px-3 rounded">Add</button>
      </div>

      <ul className="list-disc list-inside">
        {notes.map(note => (
          <li key={note.id} className="flex justify-between items-center">
            <span>{note.text}</span>
            <button onClick={() => handleDelete(note.id)} className="text-red-500">Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default NotesCard;
