// src/components/MiniCalendar.jsx
import React, { useState } from "react";

function MiniCalendar() {
  const [events, setEvents] = useState({});
  const [selectedDate, setSelectedDate] = useState(null);
  const [newEvent, setNewEvent] = useState("");

  const handleDateClick = (day) => {
    setSelectedDate(day);
    setNewEvent(events[day] || "");
  };

  const saveEvent = () => {
    setEvents({ ...events, [selectedDate]: newEvent });
    setSelectedDate(null);
    setNewEvent("");
    // TODO: send event to Django API (SQLite3)
  };

  const deleteEvent = () => {
    const updated = { ...events };
    delete updated[selectedDate];
    setEvents(updated);
    setSelectedDate(null);
    setNewEvent("");
    // TODO: delete from Django API
  };

  const days = Array.from({ length: 31 }, (_, i) => i + 1);

  return (
    <div className="bg-white shadow-md rounded-2xl p-4 border border-gray-200">
      <h2 className="text-lg font-semibold text-purple-600 mb-4">Calendar</h2>
      <div className="grid grid-cols-7 gap-2 text-center">
        {days.map((day) => (
          <div
            key={day}
            onClick={() => handleDateClick(day)}
            className="cursor-pointer p-2 rounded-md hover:bg-purple-100 transition"
          >
            {day}
            {events[day] && (
              <div className="text-xs text-purple-500">â€¢ {events[day]}</div>
            )}
          </div>
        ))}
      </div>

      {/* Modal for events */}
      {selectedDate && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40">
          <div className="bg-white rounded-xl p-6 shadow-lg w-80">
            <h3 className="text-lg font-semibold mb-4 text-purple-600">
              Events for {selectedDate}
            </h3>
            <input
              type="text"
              value={newEvent}
              onChange={(e) => setNewEvent(e.target.value)}
              className="w-full p-2 border rounded-md mb-3"
              placeholder="Enter event"
            />
            <div className="flex justify-between">
              <button
                onClick={saveEvent}
                className="bg-purple-500 text-white px-4 py-2 rounded-md"
              >
                Save
              </button>
              <button
                onClick={deleteEvent}
                className="bg-red-500 text-white px-4 py-2 rounded-md"
              >
                Delete
              </button>
              <button
                onClick={() => setSelectedDate(null)}
                className="bg-gray-300 px-4 py-2 rounded-md"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default MiniCalendar;
