import React, { useState } from "react";
import axios from "axios";

const CalendarCard = ({ events, fetchEvents }) => {
  const [newEvent, setNewEvent] = useState("");
  const [newDate, setNewDate] = useState("");

  const handleAdd = () => {
    if (!newEvent || !newDate) return;
    axios.post("http://127.0.0.1:8000/api/calendar/", { title: newEvent, date: newDate })
      .then(() => {
        setNewEvent(""); 
        setNewDate("");
        fetchEvents();
      })
      .catch(err => console.error("Error adding event:", err));
  };

  const handleDelete = (id) => {
    axios.delete(`http://127.0.0.1:8000/api/calendar/${id}/`)
      .then(() => fetchEvents())
      .catch(err => console.error("Error deleting event:", err));
  };

  return (
    <div className="bg-white rounded-2xl shadow p-4">
      <h2 className="text-xl font-semibold mb-2">Calendar</h2>

      <div className="flex gap-2 mb-2">
        <input 
          type="text" 
          placeholder="Event title" 
          className="border rounded p-1 flex-1"
          value={newEvent} 
          onChange={(e) => setNewEvent(e.target.value)} 
        />
        <input 
          type="date" 
          className="border rounded p-1" 
          value={newDate} 
          onChange={(e) => setNewDate(e.target.value)} 
        />
        <button onClick={handleAdd} className="bg-purple-500 text-white px-3 rounded">Add</button>
      </div>

      <ul className="list-disc list-inside">
        {events.map(event => (
          <li key={event.id} className="flex justify-between items-center">
            <span>{event.title} - {event.date}</span>
            <button onClick={() => handleDelete(event.id)} className="text-red-500">Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CalendarCard;
