// src/components/TasksToday.jsx
import React from "react";

function TasksToday() {
  // Example data – later you can fetch from API or database
  const tasks = [
    { task: "Finish React project", status: "In Progress" },
    { task: "Read 10 pages of book", status: "Pending" },
    { task: "Workout 30 mins", status: "Done" },
  ];

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200">
      <h2 className="text-xl font-semibold mb-4 tracking-wide">Today's Tasks</h2>
      <table className="w-full border-collapse">
        <thead>
          <tr className="text-left text-gray-600 text-sm border-b">
            <th className="pb-2">Task</th>
            <th className="pb-2">Status</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((t, index) => (
            <tr key={index} className="border-b last:border-0">
              <td className="py-2">{t.task}</td>
              <td className="py-2 text-gray-700">{t.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TasksToday;
