import { useState, useEffect } from "react";

export default function WeatherWidget() {
  const [weather, setWeather] = useState(null);

  useEffect(() => {
    // demo API: Open-Meteo (no key needed)
    fetch("https://api.open-meteo.com/v1/forecast?latitude=28.6&longitude=77.2&current_weather=true")
      .then((res) => res.json())
      .then((data) => setWeather(data.current_weather))
      .catch(() => setWeather(null));
  }, []);

  return (
    <div className="bg-white rounded-2xl shadow-lg p-6 text-center">
      <h2 className="text-xl font-bold mb-4">üå§Ô∏è Weather</h2>
      {weather ? (
        <div>
          <p className="text-2xl font-semibold">{weather.temperature}¬∞C</p>
          <p className="text-gray-500">Windspeed: {weather.windspeed} km/h</p>
        </div>
      ) : (
        <p className="text-gray-500">Fetching weather...</p>
      )}
    </div>
  );
}
