import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

// Custom hook to sync state with localStorage
function useLocalStorage(key, initialValue) {
  const [state, setState] = useState(() => {
    try {
      const stored = localStorage.getItem(key);
      return stored ? JSON.parse(stored) : initialValue;
    } catch {
      return initialValue;
    }
  });

  useEffect(() => {
    localStorage.setItem(key, JSON.stringify(state));
  }, [key, state]);

  return [state, setState];
}

// Generic list section for add, edit, delete
const WorkListSection = ({ title, storageKey, addButtonColor }) => {
  const [input, setInput] = useState("");
  const [items, setItems] = useLocalStorage(storageKey, []);
  const [editIndex, setEditIndex] = useState(null);
  const [editText, setEditText] = useState("");

  const addItem = () => {
    if (input.trim() === "") return;
    setItems([...items, input]);
    setInput("");
  };

  const deleteItem = (index) => {
    setItems(items.filter((_, idx) => idx !== index));
  };

  const startEdit = (index) => {
    setEditIndex(index);
    setEditText(items[index]);
  };

  const saveEdit = (index) => {
    const newItems = [...items];
    newItems[index] = editText;
    setItems(newItems);
    setEditIndex(null);
    setEditText("");
  };

  return (
    <div>
      <h3 className="text-lg font-semibold mb-2">{title}</h3>
      <div className="flex mb-2">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder={`Add ${title.toLowerCase()}`}
          className="flex-1 p-2 border border-gray-300 rounded-l"
        />
        <button
          onClick={addItem}
          className={`${addButtonColor} text-white px-4 rounded-r hover:brightness-110`}
        >
          Add
        </button>
      </div>
      <ul className="list-disc pl-5">
        {items.map((item, idx) => (
          <li key={idx} className="flex justify-between items-center mb-1">
            {editIndex === idx ? (
              <input
                className="flex-1 border border-gray-300 p-1 rounded"
                value={editText}
                onChange={(e) => setEditText(e.target.value)}
              />
            ) : (
              <span>{item}</span>
            )}
            <div className="flex gap-2 ml-2">
              {editIndex === idx ? (
                <button
                  onClick={() => saveEdit(idx)}
                  className="text-green-500 hover:text-green-700"
                >
                  üíæ
                </button>
              ) : (
                <button
                  onClick={() => startEdit(idx)}
                  className="text-blue-500 hover:text-blue-700"
                >
                  ‚úé
                </button>
              )}
              <button
                onClick={() => deleteItem(idx)}
                className="text-red-500 hover:text-red-700"
              >
                ‚úï
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

// Main WorkCard component
function WorkCard() {
  const [activeSections, setActiveSections] = useState([]);

  const sections = [
    {
      name: "Projects",
      icon: "üìÅ",
      component: (
        <WorkListSection
          title="Projects"
          storageKey="workProjects"
          addButtonColor="bg-blue-500"
        />
      ),
    },
    {
      name: "Tasks",
      icon: "üìù",
      component: (
        <WorkListSection
          title="Tasks"
          storageKey="workTasks"
          addButtonColor="bg-green-500"
        />
      ),
    },
    {
      name: "Reports",
      icon: "üìä",
      component: (
        <WorkListSection
          title="Reports"
          storageKey="workReports"
          addButtonColor="bg-purple-500"
        />
      ),
    },
    {
      name: "Meetings",
      icon: "üìÖ",
      component: (
        <WorkListSection
          title="Meetings"
          storageKey="workMeetings"
          addButtonColor="bg-orange-500"
        />
      ),
    },
  ];

  const toggleSection = (name) => {
    setActiveSections((prev) =>
      prev.includes(name) ? prev.filter((s) => s !== name) : [...prev, name]
    );
  };

  return (
    <div className="p-4 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Work Hub</h2>

      {/* Animated Icons */}
      <div className="flex justify-around mb-6">
        {sections.map((section) => (
          <motion.div
            key={section.name}
            onClick={() => toggleSection(section.name)}
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.95 }}
            className={`flex flex-col items-center cursor-pointer p-2 rounded transition-colors ${
              activeSections.includes(section.name)
                ? "bg-gray-200"
                : "hover:bg-gray-100"
            }`}
          >
            <span className="text-2xl">{section.icon}</span>
            <span className="mt-1 text-sm">{section.name}</span>
          </motion.div>
        ))}
      </div>

      {/* Animated Section Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <AnimatePresence>
          {sections
            .filter((section) => activeSections.includes(section.name))
            .map((section) => (
              <motion.div
                key={section.name}
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
                className="p-4 bg-gray-50 rounded shadow"
              >
                {section.component}
              </motion.div>
            ))}
        </AnimatePresence>

        {activeSections.length === 0 && (
          <p className="text-gray-500 col-span-full text-center">
            Select a section above to view content
          </p>
        )}
      </div>
    </div>
  );
}

export default WorkCard;
