import React, { useState } from "react";
import useSummary from "../hooks/useSummary"; // <-- correct import

export default function Dashboard() {
  const { summary, loading, error } = useSummary();
  const [showDetails, setShowDetails] = useState(false);

  // Toggle detailed view
  const toggleDetails = () => setShowDetails((prev) => !prev);

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem("accessToken");
    localStorage.removeItem("refreshToken");
    window.location.href = "/login"; // redirect to login page
  };

  if (loading) {
    return (
      <div className="p-4 text-center text-gray-700">
        Loading summary...
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-4 text-center text-red-500">
        Error loading summary
      </div>
    );
  }

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Dashboard Summary</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          Logout
        </button>
      </div>

      <div className="bg-white shadow-md rounded p-4">
        <h2 className="text-xl font-semibold mb-4">Summary Overview</h2>

        {/* Display summary as JSON */}
        <pre className="bg-gray-50 p-4 rounded overflow-auto">
          {JSON.stringify(summary, null, 2)}
        </pre>

        {/* Toggle details */}
        <button
          onClick={toggleDetails}
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          {showDetails ? "Hide Details" : "Show Details"}
        </button>

        {/* Detailed projects/tasks view */}
        {showDetails && (
          <div className="mt-4 bg-gray-50 p-4 rounded shadow-inner">
            <h3 className="font-semibold mb-2">Detailed View</h3>

            {summary?.projects?.length > 0 ? (
              <ul className="list-disc ml-6">
                {summary.projects.map((project, index) => (
                  <li key={index}>
                    <span className="font-medium">{project.name}</span> -{" "}
                    {project.status}
                  </li>
                ))}
              </ul>
            ) : (
              <p>No detailed projects available.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
