import { useEffect, useState } from "react";
import { fetchTodayTasks, addTask, updateTask, deleteTask } from "../api/tasks";
export default function TasksWeekly() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const today = new Date();
    const sunday = new Date(today);
    sunday.setDate(today.getDate() + (7 - today.getDay()));

    listTasks({
      ordering: "due_date",
      due_date__gte: today.toISOString().split("T")[0],
      due_date__lte: sunday.toISOString().split("T")[0],
    }).then((d) => setTasks(d.results));
  }, []);

  return (
    <div className="p-4 bg-white rounded-2xl shadow">
      <h2 className="text-lg font-semibold mb-3">This Week</h2>
      <div className="space-y-2">
        {tasks.map((t) => (
          <div
            key={t.id}
            className="flex justify-between items-center p-2 rounded-lg border"
          >
            <div className="text-sm">
              {t.title}{" "}
              {t.due_date && (
                <span className="text-xs text-gray-500">({t.due_date})</span>
              )}
            </div>
            <span
              className={`text-xs px-2 py-0.5 rounded ${
                t.status === "DONE"
                  ? "bg-green-100 text-green-700"
                  : "bg-yellow-100 text-yellow-700"
              }`}
            >
              {t.status}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
}
