import React from "react";

const QuoteWidget = ({ data }) => {
  return (
    <div className="bg-white rounded-2xl shadow p-4 text-center">
      <h2 className="text-xl font-semibold mb-2">Quote</h2>
      {data ? (
        <>
          <p className="text-gray-700 italic">"{data.text}"</p>
          {data.author && <p className="text-gray-500 mt-1">- {data.author}</p>}
        </>
      ) : (
        <p className="text-gray-400">Loading quote...</p>
      )}
    </div>
  );
};

export default QuoteWidget;
