// src/components/CalendarCard.jsx
import React, { useState } from "react";

function CalendarCard() {
  const days = Array.from({ length: 31 }, (_, i) => i + 1);

  const [events, setEvents] = useState({});
  const [selectedDay, setSelectedDay] = useState(null);
  const [inputValue, setInputValue] = useState("");

  const handleDayClick = (day) => {
    setSelectedDay(day);
    setInputValue(events[day] || "");
  };

  const handleSave = () => {
    if (inputValue.trim() === "") {
      const updated = { ...events };
      delete updated[selectedDay]; // remove event if input empty
      setEvents(updated);
    } else {
      setEvents({ ...events, [selectedDay]: inputValue });
    }
    setSelectedDay(null);
    setInputValue("");
  };

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200">
      <h2 className="text-xl font-semibold mb-4">Mini Calendar</h2>
      <div className="grid grid-cols-7 gap-2 text-center">
        {days.map((day) => (
          <div
            key={day}
            onClick={() => handleDayClick(day)}
            className={`cursor-pointer p-2 rounded-lg border ${
              events[day]
                ? "bg-blue-100 border-blue-400"
                : "bg-gray-50 border-gray-200"
            } hover:bg-blue-200`}
          >
            {day}
            {events[day] && (
              <div className="text-xs text-blue-700 mt-1 truncate">
                {events[day]}
              </div>
            )}
          </div>
        ))}
      </div>

      {selectedDay && (
        <div className="mt-4 p-4 border rounded-lg bg-gray-50">
          <h3 className="font-semibold mb-2">Events on {selectedDay}</h3>
          <input
            type="text"
            className="border p-2 rounded w-full mb-2"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder="Add or edit event"
          />
          <div className="flex gap-2">
            <button
              onClick={handleSave}
              className="bg-blue-500 text-white px-4 py-1 rounded hover:bg-blue-600"
            >
              Save
            </button>
            <button
              onClick={() => setSelectedDay(null)}
              className="bg-gray-300 px-4 py-1 rounded hover:bg-gray-400"
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default CalendarCard;
