// src/components/CalendarCard.jsx
import React, { useState } from "react";

function CalendarCard() {
  const [events, setEvents] = useState([]);
  const [date, setDate] = useState("");
  const [title, setTitle] = useState("");
  const [editIndex, setEditIndex] = useState(null);

  const handleAddEvent = () => {
    if (!date || !title) return;
    if (editIndex !== null) {
      const updated = [...events];
      updated[editIndex] = { date, title };
      setEvents(updated);
      setEditIndex(null);
    } else {
      setEvents([...events, { date, title }]);
    }
    setDate("");
    setTitle("");
  };

  const handleEdit = (index) => {
    setDate(events[index].date);
    setTitle(events[index].title);
    setEditIndex(index);
  };

  const handleDelete = (index) => {
    setEvents(events.filter((_, i) => i !== index));
  };

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200 flex flex-col">
      <h2 className="text-xl font-semibold mb-4 tracking-wide">Calendar</h2>

      {/* Input Form */}
      <div className="flex flex-col gap-2 mb-4">
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border rounded-md px-3 py-2 text-sm"
        />
        <input
          type="text"
          placeholder="Event title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border rounded-md px-3 py-2 text-sm"
        />
        <button
          onClick={handleAddEvent}
          className="bg-green-500 text-white px-4 py-2 rounded-md text-sm hover:bg-green-600"
        >
          {editIndex !== null ? "Update Event" : "Add Event"}
        </button>
      </div>

      {/* Events List */}
      <ul className="space-y-2">
        {events.length === 0 ? (
          <p className="text-gray-500 text-sm">No events added yet</p>
        ) : (
          events.map((event, index) => (
            <li
              key={index}
              className="flex justify-between items-center bg-gray-100 px-3 py-2 rounded-md"
            >
              <div>
                <p className="text-sm font-medium">{event.title}</p>
                <p className="text-xs text-gray-500">{event.date}</p>
              </div>
              <div className="flex gap-2">
                <button
                  onClick={() => handleEdit(index)}
                  className="text-blue-500 text-sm hover:underline"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(index)}
                  className="text-red-500 text-sm hover:underline"
                >
                  Delete
                </button>
              </div>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default CalendarCard;
