// src/components/NotesCard.jsx
import React, { useState } from "react";

function NotesCard() {
  const [notes, setNotes] = useState([
    
  ]);
  const [newNote, setNewNote] = useState("");
  const [editingIndex, setEditingIndex] = useState(null);
  const [editingText, setEditingText] = useState("");

  const addNote = () => {
    if (newNote.trim() !== "") {
      setNotes([...notes, newNote]);
      setNewNote("");
    }
  };

  const deleteNote = (index) => {
    const updated = [...notes];
    updated.splice(index, 1);
    setNotes(updated);
  };

  const startEditing = (index) => {
    setEditingIndex(index);
    setEditingText(notes[index]);
  };

  const saveEdit = () => {
    const updated = [...notes];
    updated[editingIndex] = editingText;
    setNotes(updated);
    setEditingIndex(null);
    setEditingText("");
  };

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200">
      <h2 className="text-xl font-semibold mb-4 tracking-wide">Notes</h2>

      {/* Add Note */}
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="Write a note..."
          value={newNote}
          onChange={(e) => setNewNote(e.target.value)}
          className="border rounded-md px-3 py-2 text-sm flex-1"
        />
        <button
          onClick={addNote}
          className="bg-purple-500 text-white px-4 py-2 rounded-md text-sm hover:bg-purple-600"
        >
          Add
        </button>
      </div>

      {/* Notes List */}
      <ul className="space-y-2 max-h-40 overflow-y-auto">
        {notes.map((note, index) => (
          <li
            key={index}
            className="flex justify-between items-center border-b pb-1"
          >
            {editingIndex === index ? (
              <div className="flex gap-2 flex-1">
                <input
                  type="text"
                  value={editingText}
                  onChange={(e) => setEditingText(e.target.value)}
                  className="border rounded-md px-2 py-1 text-sm flex-1"
                />
                <button
                  onClick={saveEdit}
                  className="text-green-500 text-xs hover:underline"
                >
                  Save
                </button>
              </div>
            ) : (
              <>
                <span className="text-sm">{note}</span>
                <div className="flex gap-2">
                  <button
                    onClick={() => startEditing(index)}
                    className="text-blue-500 text-xs hover:underline"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => deleteNote(index)}
                    className="text-red-500 text-xs hover:underline"
                  >
                    Delete
                  </button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default NotesCard;
