// src/components/PersonalCard.jsx
import React from "react";
import { FaList, FaBook, FaTasks, FaBullseye } from "react-icons/fa";

const features = [
  { icon: <FaList />, label: "To-Do" },
  { icon: <FaBook />, label: "Journal" },
  { icon: <FaTasks />, label: "Habits" },
  { icon: <FaBullseye />, label: "Goals" },
];

function PersonalCard() {
  const handleClick = (feature) => {
    alert(`Open modal for ${feature} (Add/Edit/Delete events)`);
    // Later: Replace with API call to Django
  };

  return (
    <div className="bg-white shadow-md rounded-2xl p-6 border border-gray-200">
      <h2 className="text-lg font-semibold mb-4 text-purple-600">Personal</h2>
      <div className="grid grid-cols-2 gap-4">
        {features.map((f, idx) => (
          <div
            key={idx}
            onClick={() => handleClick(f.label)}
            className="flex flex-col items-center justify-center cursor-pointer transform transition-transform duration-300 hover:scale-110"
          >
            <div className="text-3xl text-purple-500">{f.icon}</div>
            <span className="mt-2 text-sm">{f.label}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default PersonalCard;
