import React, { useState, useEffect } from "react";
import api from "../api/api"; // ← USE authenticated axios instance
import { ListTodo, Book, CheckSquare, Target } from "lucide-react";

const PersonalCard = () => {
  const [events, setEvents] = useState([]);

  // Fetch personal events
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const res = await api.get("/events/");

        // Ensure backend returned an array
        if (Array.isArray(res.data)) {
          setEvents(res.data.filter((e) => e.category === "personal"));
        } else {
          setEvents([]);
        }

      } catch (err) {
        console.error("Error fetching personal events:", err.response?.data || err);
      }
    };

    fetchEvents();
  }, []);

  // Add new personal event
  const addEvent = async (title) => {
    try {
      const res = await api.post("/events/", {
        title,
        category: "personal",
      });

      setEvents([res.data, ...events]);
    } catch (err) {
      console.error("Error adding event:", err.response?.data || err);
    }
  };

  // Delete event
  const deleteEvent = async (id) => {
    try {
      await api.delete(`/events/${id}/`);
      setEvents(events.filter((e) => e.id !== id));
    } catch (err) {
      console.error("Error deleting event:", err.response?.data || err);
    }
  };

  return (
    <div className="bg-white p-4 rounded-2xl shadow-md">

      {/* Header */}
      <div className="bg-[#c8acc8] text-black p-2 rounded-t-2xl text-center font-bold">
        Personal
      </div>

      {/* Buttons */}
      <div className="grid grid-cols-2 gap-4">
        <button
          onClick={() => addEvent("New To-Do")}
          className="flex flex-col items-center hover:scale-110 transition-transform"
        >
          <ListTodo className="text-purple-500 w-8 h-8" />
          <span>To-Do</span>
        </button>

        <button
          onClick={() => addEvent("New Journal")}
          className="flex flex-col items-center hover:scale-110 transition-transform"
        >
          <Book className="text-purple-500 w-8 h-8" />
          <span>Journal</span>
        </button>

        <button
          onClick={() => addEvent("New Habit")}
          className="flex flex-col items-center hover:scale-110 transition-transform"
        >
          <CheckSquare className="text-purple-500 w-8 h-8" />
          <span>Habits</span>
        </button>

        <button
          onClick={() => addEvent("New Goal")}
          className="flex flex-col items-center hover:scale-110 transition-transform"
        >
          <Target className="text-purple-500 w-8 h-8" />
          <span>Goals</span>
        </button>
      </div>

      {/* Event List */}
      <ul className="mt-4">
        {events.map((event) => (
          <li key={event.id} className="flex justify-between items-center">
            {event.title}
            <button
              className="text-red-500 ml-2"
              onClick={() => deleteEvent(event.id)}
            >
              ✕
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PersonalCard;
