import React from "react";

export default function TasksToday() {
  return (
    <div className="bg-[#fafafa] shadow-md rounded-xl p-6">
      <h2 className="uppercase tracking-widest text-xs mb-3 text-gray-500">— Today’s Tasks</h2>
      <table className="w-full text-left text-sm">
        <thead>
          <tr className="border-b text-gray-600">
            <th className="pb-2">Task</th>
            <th className="pb-2">Done</th>
          </tr>
        </thead>
        <tbody>
          {[...Array(4)].map((_, i) => (
            <tr key={i} className="border-b">
              <td className="py-2">Task {i + 1}</td>
              <td><input type="checkbox" className="accent-gray-600"/></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
