import React, { useEffect, useState } from "react";

export default function InspirationCard() {
  const [quote, setQuote] = useState("Loading...");
  const [image, setImage] = useState("/flower1.jpg"); // default image

  // Predefined flower/nature images (put them inside public/ folder)
  const images = ["/flower1.jpg", "/flower2.jpg", "/flower3.jpg", "/flower4.jpg","/flower5.jpg","/flower6.jpg","/flower7.jpg","/flower8.jpg","/flower9.jpg"];

  // Function to fetch new quote + random image
  const fetchQuoteAndImage = () => {
    // Fetch quote
    fetch("https://api.quotable.io/random")
      .then((res) => res.json())
      .then((data) => setQuote(data.content))
      .catch(() => setQuote("Stay positive, keep going!"));

    // Pick a random image
    const randomImg = images[Math.floor(Math.random() * images.length)];
    setImage(randomImg);
  };

  useEffect(() => {
    fetchQuoteAndImage(); // load first quote + image
  }, []);

  return (
    <div className="bg-[#fafafa] shadow-md rounded-xl overflow-hidden text-center">
      {/* Flower / Nature Image */}
      <img
        src={image}
        alt="Inspiration"
        className="w-full h-72 object-cover"
      />

      {/* Quote */}
      <p className="p-4 italic text-sm text-gray-700 font-serif">
        "{quote}"
      </p>

      {/* Refresh Button */}
      <button
        onClick={fetchQuoteAndImage}
        className="mb-4 px-4 py-2 bg-pink-200 hover:bg-pink-300 text-gray-800 rounded-lg transition duration-200"
      >
        ðŸ”„ New Quote
      </button>
    </div>
  );
}
