// src/components/InspirationCard.jsx
import React from "react";

function InspirationCard() {
  return (
    <div className="relative bg-white shadow-md rounded-2xl overflow-hidden border border-gray-200 col-span-1 lg:col-span-2">
      {/* Background image */}
      <div
        className="h-48 bg-cover bg-center"
        style={{
          backgroundImage:
            "url('https://images.unsplash.com/photo-1507525428034-b723cf961d3e?auto=format&fit=crop&w=1200&q=80')",
        }}
      ></div>

      {/* Overlay text */}
      <div className="absolute inset-0 bg-black/30 flex items-center justify-center">
        <h2 className="text-white text-2xl md:text-3xl font-bold text-center px-4">
          "Believe in yourself and all that you are."
        </h2>
      </div>
    </div>
  );
}

export default InspirationCard;
