import { useState, useEffect } from "react";

export default function WeatherWidget() {
  const [weather, setWeather] = useState({ temp: 25, city: "Delhi" });

  useEffect(() => {
    // Later you can call real API (OpenWeatherMap)
    // fetch("api...")
  }, []);

  return (
    <div className="bg-blue-500 text-white rounded-2xl p-4 shadow">
      <h2 className="text-xl font-bold mb-2">☁ Weather</h2>
      <p className="text-lg">{weather.city}</p>
      <p className="text-3xl font-bold">{weather.temp}°C</p>
    </div>
  );
}
