import { useState } from "react";

export default function NotesWidget() {
  const [notes, setNotes] = useState([]);
  const [input, setInput] = useState("");

  const addNote = () => {
    if (input.trim() === "") return;
    setNotes([...notes, input]);
    setInput("");
  };

  return (
    <div className="bg-white rounded-2xl shadow-lg p-6">
      <h2 className="text-xl font-bold mb-4">ðŸ“’ Notes</h2>
      <textarea
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Write a note..."
        className="w-full border rounded-lg px-3 py-2 mb-3 focus:outline-none focus:ring-2 focus:ring-blue-400"
      />
      <button
        onClick={addNote}
        className="w-full py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
      >
        Save Note
      </button>

      <ul className="mt-4 space-y-2">
        {notes.map((note, index) => (
          <li
            key={index}
            className="bg-gray-100 p-3 rounded-lg shadow-sm text-gray-800"
          >
            {note}
          </li>
        ))}
      </ul>
    </div>
  );
}
