import React from "react";
import { Card, CardContent } from "../components/ui/card";
import { StickyNote, Quote } from "lucide-react";

const PersonalCard = ({ notes = [], quotes = [] }) => {
  return (
    <Card className="p-4 rounded-2xl shadow-md bg-white">
      <h2 className="text-xl font-semibold mb-3">Personal Space</h2>
      <CardContent className="space-y-3">
        {/* Notes Section */}
        <div>
          <h3 className="flex items-center gap-2 font-medium text-gray-700">
            <StickyNote size={18} /> Notes
          </h3>
          {notes.length > 0 ? (
            <ul className="list-disc pl-6 text-gray-600">
              {notes.slice(0, 3).map((note) => (
                <li key={note.id}>{note.content}</li>
              ))}
            </ul>
          ) : (
            <p className="text-sm text-gray-400">No notes available</p>
          )}
        </div>

        {/* Quotes Section */}
        <div>
          <h3 className="flex items-center gap-2 font-medium text-gray-700">
            <Quote size={18} /> Quotes
          </h3>
          {quotes.length > 0 ? (
            <p className="italic text-gray-600">
              “{quotes[0].text}”
            </p>
          ) : (
            <p className="text-sm text-gray-400">No quotes available</p>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

export default PersonalCard;
